INSERT INTO blog_post_comments (id, post_id, user_id, content, has_moderated, created_at, updated_at) VALUES (1, 12, 1, 'Равным образом консультация с широким активом требуют определения и уточнения модели развития. Товарищи! консультация с широким активом позволяет выполнять важные задания по разработке систем массового участия. С другой стороны укрепление и развитие структуры обеспечивает участие в формировании систем массового участия.', 1, '2021-05-21 16:07:07', '2021-05-21 16:07:15');
INSERT INTO blog_post_comments (id, post_id, user_id, content, has_moderated, created_at, updated_at) VALUES (2, 12, 8, 'Повседневная практика показывает, что реализация намеченных плановых заданий в значительной степени обуславливает создание модели развития. Идейные соображения высшего порядка, а также дальнейшее развитие различных форм деятельности позволяет оценить значение новых предложений.', 1, '2021-05-21 16:09:07', '2021-05-21 16:09:10');